INSERT INTO `product` (`id`, `name`, `cost`, `price`, `description`, `thumb`, `images`, `update`, `show`, `priority`, `view`, `brand_id`, `color_id`) VALUES
	(1, 'Giày Vans Old Skool Đen, Bản Cổ Điển', 900000, 850000, '<p>Số lượng giới hạn ! Sản phẩm có số lượng giới hạn và thường sẽ hết mà không có thông báo trước.</div><div>Đổi Trả Dễ Dàng - Dễ dàng đổi nếu size không vừa hoặc sản phẩm lỗi sau khi nhận hàng.<br></div><div>Giao Hàng Toàn Quốc - Đơn hàng đặt trước 17h sẽ được chốt ngay trong ngày và đến tay quí khách trong khoảng 3-5 ngày. Khu vực nội thành HCM giao nhanh trong vòng 2h.<br></div><div>Có Sẵn Tại Cửa Hàng - Khách hàng tại khu vực HCM có thể đến trực tiếp cửa hàng của chúng tôi.</p>', 'assets/img/product/1/1224.jpg', '["assets/img/product/1/1224.jpg", "assets/img/product/1/4790.jpg", "assets/img/product/1/389.jpg", "assets/img/product/1/7299.jpg", "assets/img/product/1/7751.jpg", "assets/img/product/1/9031.jpg"]', '2020-11-18 00:00:00', NULL, NULL, 26, 6, 1),
	(2, 'Giày Trắng Vans Old Skool True White', 900000, 850000, '<p>Fullbox Old Skool Full White Trắng. Phù hợp: nam nữ, đi học, đi làm, hoạt động thể thao. Size: 36-44. Chất liệu: Canvas. Giao hàng toàn quốc. Bảo hành 3 tháng. Đổi trả dễ dàng. Streetwear, trẻ trung năng động.</p>', 'assets/img/product/2/1.jpg', '["assets/img/product/2/1.jpg", "assets/img/product/2/2.jpg", "assets/img/product/2/3.jpg", "assets/img/product/2/4.jpg"]', '2020-11-04 00:00:00', NULL, NULL, 94, 6, 10),
	(3, 'Giày Nike Air Force 1 ‘Just Do It’', 2100000, 1990000, '<p>Air Force 1 “JUST DO IT” được coi là một trong những đứa con cưng của N.ike, góp phần đưa khách hàng đến gần hơn với nhà sản xuất. Thương hiệu và uy tín của hãng vì thế mà cũng ngày càng được đánh giá cao hơn sau sự kiện ra mắt của dòng sản phẩm đẳng cấp này.Dòng giày này không phải là quá mới mẻ với các tín đồ giày nhưng sức hút của nó thì chưa bao giờ mất đi. Bạn có thể sử dụng giày để đi hàng ngày và đặc biệt có thể trưng diện vào những dịp đặc biệt, lễ hội, tiệc tùng một cách ấn tượng, không cầu kỳ nhưng vẫn luôn thể hiện được đẳng cấp riêng của mình.</p>', 'assets/img/product/3/1.jpg', '["assets/img/product/3/1.jpg", "assets/img/product/3/2.jpg", "assets/img/product/3/3.jpg", "assets/img/product/3/4.jpg", "assets/img/product/3/5.jpg", "assets/img/product/3/6.jpg", "assets/img/product/3/7.jpg", "assets/img/product/3/8.jpg", "assets/img/product/3/9.jpg"]', '2020-11-04 00:00:00', NULL, NULL, 49, 1, 10),
	(4, 'Giày Adidas Alphabounce Beyond Cloud White', 1500000, 1150000, '<p>Fullbox Alphabounce Beyond. 2 ver Trắng / Đen. Phù hợp: nam nữ, đi học, đi làm, tập gym. Size: 36-44. Êm chân, thoáng khí. Giao hàng toàn quốc. Bảo hành 3 tháng. Đổi trả dễ dàng. Streetwear, trẻ trung năng động.</p>', 'assets/img/product/4/1.jpg', '["assets/img/product/4/1.jpg", "assets/img/product/4/2.jpg", "assets/img/product/4/3.jpg", "assets/img/product/4/4.jpg"]', '2020-11-18 20:48:21', NULL, NULL, 25, 3, 10),
	(5, 'Giày Nike Air Force 1 Low GS Triple White', 1000000, 750000, '<p>Air Force 1 (AF1) là một trong những mẫu sneaker kinh điển mọi thời đại của thương hiệu này. Là một trong những item được cả những người sành giày và các bạn trẻ yêu thích phong cách thời trang đường phố trên khắp thế giới yêu thích. Thiết kế trẻ trung với duy nhất một màu trắng chính là điểm nhấn đặc biệt. Mọi thứ bạn trải qua trên đôi chân sẽ được lưu lại vĩnh viễn.</p>', 'assets/img/product/5/1.jpg', '["assets/img/product/5/1.jpg", "assets/img/product/5/2.jpg", "assets/img/product/5/3.jpg", "assets/img/product/5/4.jpg", "assets/img/product/5/5.jpg", "assets/img/product/5/6.jpg"]', '2020-11-18 20:54:48', NULL, NULL, 63, 1, 10),
	(6, 'Giày Addias Stan Smith Fairway', 850000, 790000, '<p>Fullbox Stan Smith. 2 ver Trắng Gót Xanh/ Full White. Thiết kế basic trend dài dài. Phù hợp: nam nữ, đi học, đi làm, hoạt động thể thao. Size: 36-44. Chất liệu: Da. Giao hàng toàn quốc. Bảo hành 3 tháng. Đổi trả dễ dàng. Streetwear, trẻ trung năng động.</p>', 'assets/img/product/6/1.jpg', '["assets/img/product/6/1.jpg", "assets/img/product/6/2.jpg", "assets/img/product/6/3.jpg", "assets/img/product/6/4.jpg"]', '2020-11-18 20:54:48', NULL, NULL, 31, 3, 10),
	(7, 'Giày Trắng Adidas Stan Smith Triple', 850000, 790000, '<p>Fullbox Stan Smith. 2 ver Trắng Gót Xanh/ Full White. Thiết kế basic trend dài dài. Phù hợp: nam nữ, đi học, đi làm, hoạt động thể thao. Size: 36-44. Chất liệu: Da. Giao hàng toàn quốc. Bảo hành 3 tháng. Đổi trả dễ dàng. Streetwear, trẻ trung năng động.</p>', 'assets/img/product/7/1.jpg', '["assets/img/product/7/1.jpg", "assets/img/product/7/2.jpg", "assets/img/product/7/3.jpg", "assets/img/product/7/4.jpg", "assets/img/product/7/5.jpg"]', '2020-11-18 20:54:48', NULL, NULL, 39, 3, 10),
	(8, 'Giày Trắng Addias Superstar Running', 850000, 750000, '<p>Fullbox Superstar Trắng. Điểm nhấn: mũi giày, tem vàng. Style basic trend dài dài. Phù hợp: nam nữ, đi học, đi làm, hoạt động thể thao. Size: 36-44. Chất liệu: Da Trơn. Giao hàng toàn quốc. Bảo hành 3 tháng. Đổi trả dễ dàng.</p>', 'assets/img/product/8/1.jpg', '["assets/img/product/8/1.jpg", "assets/img/product/8/2.jpg", "assets/img/product/8/3.jpg", "assets/img/product/8/4.jpg"]', '2020-11-18 20:54:48', NULL, NULL, 63, 3, 10),
	(9, 'Giày Trắng Nike Air Max 97 Ultra 17 Triple', 1100000, 1000000, '<p>Air Max 97 có thiết kế lấy cảm hứng từ chiếc tàu siêu tốc – bullet train của Nhật và cấu tạo từ chất liệu mesh. Một dòng air max trong bộ sưu tập air max huyền thoại của thương hiệu này. Toàn bộ đế giày được lót đệm khí so với những phiên bản air max đời trước. Vừa cổ điển vừa hiện đại, năng động, sáng tạo là tất cả những gì có thể nói về Air Max 97 – sở hữu vẻ đặc trưng , nổi bật không thể lẫn lộn.</p>', 'assets/img/product/9/1.jpg', '["assets/img/product/9/1.jpg", "assets/img/product/9/2.jpg", "assets/img/product/9/3.jpg"]', '2020-11-18 20:54:48', NULL, NULL, 25, 1, 10),
	(10, 'Giày Đen Nike Air Max 97 Ultra 17 Triple', 1100000, 1000000, '<p>Air Max 97 có thiết kế lấy cảm hứng từ chiếc tàu siêu tốc – bullet train của Nhật và cấu tạo từ chất liệu mesh. Một dòng air max trong bộ sưu tập air max huyền thoại của thương hiệu này. Toàn bộ đế giày được lót đệm khí so với những phiên bản air max đời trước. Vừa cổ điển vừa hiện đại, năng động, sáng tạo là tất cả những gì có thể nói về Air Max 97 – sở hữu vẻ đặc trưng , nổi bật không thể lẫn lộn.</p>', 'assets/img/product/10/1.jpg', '["assets/img/product/10/1.jpg", "assets/img/product/10/2.jpg", "assets/img/product/10/3.jpg"]', '2020-11-18 20:54:48', NULL, NULL, 74, 1, 1),
	(11, 'Giày Trắng New Balance CRT 300 Beige Navy', 1500000, 1250000, '<p>Fullbox NB CRT 300. 3 ver Gót Đỏ / Xanh Navy / Full White. Phù hợp: nam nữ, đi học, đi làm, hoạt động thể thao. Size: 36-44. Chất liệu: Da lộn. Giao hàng toàn quốc. Bảo hành 3 tháng. Đổi trả dễ dàng. Streetwear, trẻ trung năng động.</p>', 'assets/img/product/11/1.jpg', '["assets/img/product/11/1.jpg", "assets/img/product/11/2.jpg", "assets/img/product/11/3.jpg"]', '2020-11-18 20:54:48', NULL, NULL, 53, 8, 10),
	(12, 'Giày Addias Prophere Grey Solar Red', 1500000, 1050000, '<p>Fullbox Prophere. 3 ver Trắng / Đen / Xám. Đế giày tăng chiều cao. Phù hợp: nam nữ, đi học, đi làm, hoạt động thể thao. Size: 36-44. Giao hàng toàn quốc. Bảo hành 3 tháng. Đổi trả dễ dàng. Streetwear, trẻ trung năng động.</p>', 'assets/img/product/12/1.jpg', '["assets/img/product/12/1.jpg", "assets/img/product/12/2.jpg", "assets/img/product/12/3.jpg"]', '2020-11-18 20:54:48', NULL, NULL, 27, 3, 10),
	(13, 'Giày Nữ Nike Air Force 1 Shadow Pale Ivory.', 1300000, 1190000, '<p>Giày sneaker Nike Air Force 1. Phong cách trẻ trung năng động. Thích hợp Nữ du lịch, đi học, chơi thể thao,… Đế Double Air nâng dáng.</p>', 'assets/img/product/13/1.jpg', '["assets/img/product/13/1.jpg", "assets/img/product/13/2.jpg", "assets/img/product/13/3.jpg", "assets/img/product/13/4.jpg", "assets/img/product/13/5.jpg", "assets/img/product/13/6.jpg", "assets/img/product/13/7.jpg"]', '2020-11-18 20:54:48', NULL, NULL, 82, 1, 10),
	(14, 'Giày Adidas NMD R1 Grey', 1300000, 1190000, '<p>Fullbox NMD R1. 2 màu Xám / Trắng. Đế boost đàn hồi êm chân. Phù hợp: nam nữ, đi học, đi làm, hoạt động thể thao. Size: 36-44. Chất liệu: Vải dệt Primeknit. Giao hàng toàn quốc. Bảo hành 3 tháng. Đổi trả dễ dàng.</p>', 'assets/img/product/14/1.jpg', '["assets/img/product/14/1.jpg", "assets/img/product/14/2.jpg", "assets/img/product/14/3.jpg", "assets/img/product/14/4.jpg"]', '2020-11-18 20:54:48', NULL, NULL, 28, 3, 10),
	(15, 'Giày Nike Air Force 1 ’07 LV8 Overbranding.', 1300000, 1190000, '<p>Fullbox A.i.r Force 1 ’07 LV8 Utility. Basic mà style cực kì. Phù hợp: nam nữ, đi học, đi làm, hoạt động thể thao. Size: 36-44. Chất liệu: Da. Giao hàng toàn quốc. Bảo hành 3 tháng. Đổi trả dễ dàng. Streetwear, trẻ trung năng động.</p>', 'assets/img/product/15/1.jpg', '["assets/img/product/14/1.jpg", "assets/img/product/14/2.jpg", "assets/img/product/14/3.jpg", "assets/img/product/14/4.jpg", "assets/img/product/14/5.jpg", "assets/img/product/14/6.jpg"]', '2020-11-18 20:54:48', NULL, NULL, 45, 3, 10),
	(16, 'Giày Balenciaga Triple S Trainer phiên bản xanh đỏ 2018', 1659000, 1590000, '<p>Fullbox Balen Triple S Trainer Blue Red 2018. Dad Shoe. Đế giày tăng chiều cao. Phù hợp: nam nữ, đi học, đi làm, hoạt động thể thao. Size: 36-44. Chất liệu: Da. Giao hàng toàn quốc. Bảo hành 3 tháng. Đổi trả dễ dàng. Streetwear, trẻ trung năng động.</p>', 'assets/img/product/16/18 (1).jpg', '["assets/img/product/16/18 (1).jpg", "assets/img/product/16/18 (2).jpg", "assets/img/product/16/18 (3).jpg", "assets/img/product/16/18 (4).jpg"]', '2020-12-07 15:50:57', NULL, NULL, 0, 9, 11),
	(17, 'Balenciaga Triple S Trainer phiên bản màu trắng 2018', 1659000, 1590000, '<p>Đây là thương hiệu thời trang lâu đời nổi tiếng, cao cấp nhất nhì thế giới được thành lập từ năm 1919. Tiếp nối sự thành công, thương hiệu tiếp tục tung ra Triple S – dòng giày đa sắc màu, phá bỏ mọi giới hạn, đủ đẹp, đủ chất để khiến giới mộ điệu lại một lần nữa phải điên đảo Đây là mẫu giày cũng được nhiều người nghệ sĩ thế giới lựa chọn. Tại Việt Nam, giày Triple S được rất nhiều nghệ sĩ nổi tiếng yêu thích.</p>', 'assets/img/product/17/19 (1).jpg', '["assets/img/product/17/19 (1).jpg", "assets/img/product/17/19 (2).jpg", "assets/img/product/17/19 (3).jpg", "assets/img/product/17/19 (4).jpg"]', '2020-12-07 15:15:07', NULL, NULL, 0, 9, 10),
	(18, 'Balenciaga Triple S Trainer phiên bản màu xám 2018', 1659000, 1590000, '<p>Balenciaga Triple S Trainer phiên bản màu xám 2018 khi đập vào mắt bạn là 1 thiết kế hầm hố có có thiết kế khá hiếm hoi về độ đồ sộ của em nó, Được xem là 1 trong những mẫu giày sang chảnh nhất cho tới thời điểm hiện tại bây giờ, Balenciaga Triple S Trainer vẫn là dòng được yêu thích nhiều nhất cũng như độ hót của nó.</p>', 'assets/img/product/18/20 (1).jpg', '["assets/img/product/18/20 (1).jpg", "assets/img/product/18/20 (2).jpg", "assets/img/product/18/20 (3).jpg"]', '2020-12-07 15:19:39', NULL, NULL, 0, 9, 16),
	(19, 'Nike Air Jordan 1 X Off-White X Retro High OG  Trắng', 1659000, 1590000, '<p>Giày Off White X Air Jordan là một sản phẩm kết hợp xu hướng Off White đang thống trị xu thế đường phố vẫn với tinh thần Giày Air Jordan kèm với một số chi tiết nổi bật đặc trưng của Off White. Chính những điều đó đã mang đến nét mới lạ cho Air Jordan.</p>', 'assets/img/product/19/21 (1).jpg', '["assets/img/product/19/21 (1).jpg", "assets/img/product/19/21 (2).jpg", "assets/img/product/19/21 (3).jpg", "assets/img/product/19/21 (4).jpg", "assets/img/product/19/21 (5).jpg"]', '2020-12-07 15:30:55', NULL, NULL, 0, 1, 10),
	(20, 'Giày Chunky Sneaker Nữ Màu Trắng, Giày Thể Thao Đế Cao', 415000, 335000, '<p>Giày sneaker thể thao nữ trắng.Phong cách trẻ trung năng động.Thích hợp khi tham gia các hoạt động vui chơi, du lịch, thể thao,Chất liệu thân giày: Da PU, Vải lưới. Chất liệu đế: Cao su tổng hợp bền chắc.</p>', 'assets/img/product/20/22 (1).jpg', '["assets/img/product/20/22 (1).jpg", "assets/img/product/20/22 (2).jpg", "assets/img/product/20/22 (3).jpg", "assets/img/product/20/22 (4).jpg", "assets/img/product/20/22 (5).jpg"]', '2020-12-07 15:40:01', NULL, NULL, 0, 10, 10),
	(21, 'Nike M2K Tekno Phantom màu cam', 1390000, 1190000, '<p>Fullbox M2K Tekno Platinum Tint Celery. Phù hợp: Nam Nữ, đi học, đi làm, hoạt động thể thao. Size: 36-44. Chất liệu: Da. Giao hàng toàn quốc. Bảo hành 3 tháng. Đổi trả dễ dàng. Streetwear, trẻ trung năng động.</p>', 'assets/img/product/21/23 (1).jpg', '["assets/img/product/21/23 (1).jpg", "assets/img/product/21/23 (2).jpg", "assets/img/product/21/23 (3).jpg", "assets/img/product/21/23 (4).jpg", "assets/img/product/21/23 (5).jpg"]', '2020-12-07 15:48:34', NULL, NULL, 0, 1, 13),
	(22, 'Giày Yeezy Boost 700 Inertia', 1450000, 1390000, '<p>Yeezy Boost 700 với thiết kế thông minh, gam màu tinh tế, chất liệu bằng vải Primeknit bền nhẹ nhằm giảm tối đa trọng lượng giày khi mang nên không thể phủ nhận độ HOT của Yz .Công nghệ chống nhấc gót và đế Boost êm ái trợ lực giúp ích tuyệt đối cho những ai thường xuyên phải vận động.</p>', 'assets/img/product/22/16 (2).jpg', '["assets/img/product/22/16 (2).jpg", "assets/img/product/22/16 (3).jpg", "assets/img/product/22/16 (4).jpg"]', '2020-12-07 16:09:16', NULL, NULL, 0, 3, 11),
	(23, 'Giày Adidas Falcon Crystal White Navy', 1390000, 1290000, '<p>Adidas không khiến fans của hãng giày 3 sọc này thất vọng khi liên tiếp cho release những mẫu giày độc đáo đậm tính thời trang và luôn cháy hàng trên mọi mặt trận. Falcon là ví dụ điển hình cho xu hướng sáng tạo và học hỏi không ngừng của hãng giày đạt doanh thu số 1 toàn cầu năm 2019. Kyile Jenner đã trở thành người quảng bá hình ảnh cho dòng giày ra mắt vào mùa Hè năm nay. Phủ sóng trên mọi mặt trận thời trang và là item không thể thiếu trong tủ giày. Có thể nói Adidas Falcon là mẫu giày bạn không thể bỏ lỡ.</p>', 'assets/img/product/23/17 (1).jpg', '["assets/img/product/23/17 (1).jpg", "assets/img/product/23/17 (2).jpg", "assets/img/product/23/17 (3).jpg", "assets/img/product/23/17 (4).jpg"]', '2020-12-07 16:12:40', NULL, NULL, 0, 3, 10),
	(24, 'Giày Balenciaga Triple S Clear Sole màu hồng', 1390000, 1290000, '<p><p>Đây là thương hiệu thời trang lâu đời nổi tiếng, cao cấp nhất nhì thế giới được thành lập từ năm 1919. Tiếp nối sự thành công, thương hiệu tiếp tục tung ra Triple S – dòng giày đa sắc màu, phá bỏ mọi giới hạn, đủ đẹp, đủ chất để khiến giới mộ điệu lại một lần nữa phải điên đảo Đây là mẫu giày cũng được nhiều người nghệ sĩ thế giới lựa chọn. Tại Việt Nam, giày Triple S được rất nhiều nghệ sĩ nổi tiếng yêu thích.</p></p>', 'assets/img/product/24/24 (1).jpg', '["assets/img/product/24/24 (1).jpg", "assets/img/product/24/24 (2).jpg", "assets/img/product/24/24 (3).jpg", "assets/img/product/24/24 (4).jpg"]', '2020-12-07 16:18:57', NULL, NULL, 0, 9, 17),
	(25, 'Giày Converse Chuck 70 High Top Sunflower (1970s)', 950000, 890000, '<p>Phiên bản cổ cao màu vàng cá tính của Chuck 1970s được giới trẻ nhiệt tình ưu ái bởi sự hiện đại, tiện dụng. Thay đổi ở đế giày với màu trắng ngà và cao hơn so với các phiên bản khác. Phần thân giày có màu sắc đồng bộ với đế giày và có độ thanh mảnh hơn. Chất vải Canvas dày, nhẹ, bền chắc giúp người dùng có được sự thoải mái. Đường chỉ trắng chạy dọc thân giày được chăm chút tỉ mỉ hơn rất nhiều.</p>', 'assets/img/product/25/25 (2).jpg', '["assets/img/product/25/25 (1).jpg", "assets/img/product/25/25 (2).jpg", "assets/img/product/25/25 (3).jpg", "assets/img/product/25/25 (4).jpg"]', '2020-12-07 16:32:20', NULL, NULL, 0, 4, 14),
	(26, 'Giày Converse Chuck Taylor All-Star 70s Hi Comme Des Garcons PLAY Black (1970s CDG)', 1160000, 990000, '<p>Đôi giày CDG Play x CV 1970 được ra mắt lần đầu vào năm 2013. Sự kết hợp này không chỉ mang đến một hình ảnh mới mẻ cho CV 1970 cũng như CDG Play mà còn tạo được dấu ấn khá rõ nét trong lòng những ai đam mê sneakers. Đơn giản mà hiệu quả, nắm bắt được tâm lý này của giới đam mê thời trang và sneakers toàn thế giới, COMME des GARÇONS Play và CV 1970 thật sự đã mang đến một làn gió mới cho những ai yêu thích sự giản đơn nhưng vẫn thể hiện được phong cách riêng và sự sáng tạo trong đó.</p>', 'assets/img/product/26/26 (1).jpg', '["assets/img/product/26/26 (1).jpg", "assets/img/product/26/26 (2).jpg", "assets/img/product/26/26 (3).jpg", "assets/img/product/26/26 (4).jpg"]', '2020-12-07 16:49:24', NULL, NULL, 0, 4, 1),
	(27, 'Giày Converse Chuck 70 X PEACEMINUSONE (1970s)', 1390000, 1290000, '<p>Khoen xỏ lỗ giày và khoen bên hông giày được làm từ kim loại cao cấp chống rỉ sét.Phần thân giày có màu sắc đồng bộ với đế giày và có độ thanh mảnh hơn. Chất vải Canvas dày, nhẹ, bền chắc giúp người dùng có được sự thoải mái. Đường chỉ trắng chạy dọc thân giày được chăm chút tỉ mỉ hơn rất nhiều.Kiểu dáng retro vintage huyền thoại.Chất vải canvas dày dặn 340g, form giày cứng cáp.Đế giày cao 3.5cm màu trắng ngà và được phủ bóng để dễ dàng dễ vệ sinh.Đệm chân Ortholite mềm mại hỗ trợ đi lại cả ngày.Khoen xỏ lỗ giày và khoen bên hông giày được làm từ kim loại cao cấp chống rỉ sét</p>', 'assets/img/product/27/27 (1).jpg', '["assets/img/product/27/27 (1).jpg", "assets/img/product/27/27 (2).jpg", "assets/img/product/27/27 (3).jpg", "assets/img/product/27/27 (4).jpg"]', '2020-12-07 16:53:07', NULL, NULL, 0, 4, 1),
	(28, 'Giày New Balance 996 Navy', 1390000, 1190000, '<p>Dễ dàng nhận thấy ngôn ngữ thiết kế của NB luôn xuyên suốt với nhau, vẫn “mô tuýp” ấy, vẫn những chất liệu ấy, chỉ nhấn nhá một chút sự khác biệt trên mỗi đôi giày nhưng không vì thế mà làm cho nó trở nên nhàm chán, tất đều có những nét quyến rũ, hấp dẫn của riêng mình.</p>', 'assets/img/product/28/28 (1).jpg', '["assets/img/product/28/28 (1).jpg", "assets/img/product/28/28 (2).jpg", "assets/img/product/28/28 (3).jpg", "assets/img/product/28/28 (4).jpg", "assets/img/product/28/28 (5).jpg"]', '2020-12-07 16:56:24', NULL, NULL, 0, 8, 11),
	(29, 'Giày New Balance 574 Grey. Giày Sneaker, Giày Thể Thao New Balance Nam Nữ Xám', 1390000, 1290000, '<p>Dễ dàng nhận thấy ngôn ngữ thiết kế của NB luôn xuyên suốt với nhau, vẫn “mô tuýp” ấy, vẫn những chất liệu ấy, chỉ nhấn nhá một chút sự khác biệt trên mỗi đôi giày nhưng không vì thế mà làm cho nó trở nên nhàm chán, tất đều có những nét quyến rũ, hấp dẫn của riêng mình.</p>', 'assets/img/product/29/29 (1).jpg', '["assets/img/product/29/29 (1).jpg", "assets/img/product/29/29 (2).jpg", "assets/img/product/29/29 (3).jpg", "assets/img/product/29/29 (4).jpg"]', '2020-12-07 16:58:58', NULL, NULL, 0, 8, 16),
	(30, 'Giày Vans Style 36 “Marshmallow” Jolly xanh lá', 1060000, 890000, '<p>Van.s Style 36 Marshmallow: đôi Vans đẹp khó cưỡng. Van.s là một trong những cái tên gắn liền với nhiều thế hệ học sinh, sinh viên nhất. Đặc biệt là trong vài năm trở lại đây, Van.s lại tạo nên một cơn sốt sneaker với nhiều bạn trẻ châu Á, trong đó có cả Việt Nam. Điểm chung trong hầu hết các mẫu giày của Van.s là thiết kế đơn giản, trẻ trung lại dễ ứng dụng. Vì thế, dù không có quá nhiều cải tiến về phom dáng mà chỉ đẩy mạnh vào việc thay đổi màu sắc, họa tiết trong suốt nhiều năm qua nhưng giày của Van.s vẫn được mến mộ. Giờ đây, Van.s là một trong những items gần như “phải có” trong tủ giày của mỗi bạn trẻ.</p>', 'assets/img/product/30/30 (1).jpg', '["assets/img/product/30/30 (1).jpg", "assets/img/product/30/30 (2).jpg", "assets/img/product/30/30 (3).jpg", "assets/img/product/30/30 (4).jpg", "assets/img/product/30/30 (5).jpg"]', '2020-12-07 17:02:57', NULL, NULL, 0, 6, 15);
